mapper.select:
/* 상품 별 프로모션 적용 대상 조회 */ SELECT distinct ccb.prm_no , cpb.prm_nm , cpb.prm_kind_cd , cpb.prm_priod_ccd , cpb.prm_strt_dt , cpb.prm_end_dt , cpb.prm_std_dd , cpb.emp_yn , cpb.dc_ccd , cpb.dc_val , cpb.min_pur_amt , cpb.max_dc_amt , cpb.use_yn , cpb.rmk , cpat.aply_tgt_no FROM cc_prm_base cpb INNER JOIN cc_prm_aply_tgt cpat ON cpb.prm_no = cpat.prm_no INNER JOIN cc_cpn_base ccb ON cpb.prm_no = ccb.prm_no INNER JOIN cc_cpn_issue cci ON ccb.prm_no = cci.prm_no WHERE 1=1 AND cpb.use_yn = 'Y' AND cpat.use_yn = 'Y'
AND cpat.aply_tgt_no IN
#{item.goodsNo}
AND cci.mbr_no = #{mbrNo} AND cci.cpn_use_dt ISNULL AND NOT EXISTS( SELECT 1 FROM cc_cpn_issue a WHERE a.org_cpn_iss_no = cci.cpn_iss_no)
