mapper.select:
/* 상품 별 프로모션 적용 대상 조회 */ SELECT distinct ccb.prm_no , cpb.prm_nm , cpb.prm_kind_cd , cpb.prm_priod_ccd , cpb.prm_strt_dt , cpb.prm_end_dt , cpb.prm_std_dd , cpb.emp_yn , cpb.dc_ccd , cpb.dc_val , cpb.min_pur_amt , cpb.max_dc_amt , cpb.use_yn , cpb.rmk , cci.cpn_iss_no , cpat.aply_tgt_no FROM cc_prm_base cpb INNER JOIN cc_prm_aply_tgt cpat ON cpb.prm_no = cpat.prm_no INNER JOIN cc_cpn_base ccb ON cpb.prm_no = ccb.prm_no INNER JOIN cc_cpn_issue cci ON ccb.prm_no = cci.prm_no WHERE 1=1 AND cpb.use_yn = 'Y' AND cpb.prm_kind_cd = #{prmKindCd} AND ((cpb.prm_priod_ccd = '10' AND NOW() >= cpb.prm_strt_dt AND NOW() < cpb.prm_end_dt + interval '1 day') OR (cpb.prm_priod_ccd = '20' AND NOW() >= cci.sys_mod_dtime AND NOW() < cci.sys_mod_dtime + (interval '1 day' * cpb.prm_std_dd) + interval '1 day')) AND cpat.use_yn = 'Y' -- 구분코드 케이스문 넣기 !!
AND cpat.aply_tgt_no IN
#{item.goodsNo}
AND cpat.aply_tgt_ccd = '10'
AND ccb.cpn_kind_cd = #{cpnKindCd} AND ccb.degr_ccd = #{degrCcd} AND cci.mbr_no = #{mbrNo} AND cci.cpn_use_dt ISNULL

/* 상품 별 프로모션 적용 대상 조회 */ SELECT distinct ccb.prm_no , cpb.prm_nm , cpb.prm_kind_cd , cpb.prm_priod_ccd , cpb.prm_strt_dt , cpb.prm_end_dt , cpb.prm_std_dd , cpb.emp_yn , cpb.dc_ccd , cpb.dc_val , cpb.min_pur_amt , cpb.max_dc_amt , cpb.use_yn , cpb.rmk , cci.cpn_iss_no -- , cpat.aply_tgt_no -- FROM cc_prm_base cpb INNER JOIN cc_prm_aply_tgt cpat ON cpb.prm_no = cpat.prm_no INNER JOIN cc_cpn_base ccb ON cpb.prm_no = ccb.prm_no INNER JOIN cc_cpn_issue cci ON ccb.prm_no = cci.prm_no WHERE 1=1 AND cpb.use_yn = 'Y' AND cpb.prm_kind_cd = #{prmKindCd} AND ((cpb.prm_priod_ccd = '10' AND NOW() >= cpb.prm_strt_dt AND NOW() < cpb.prm_end_dt + interval '1 day') OR (cpb.prm_priod_ccd = '20' AND NOW() >= cci.sys_mod_dtime AND NOW() < cci.sys_mod_dtime + (interval '1 day' * cpb.prm_std_dd) + interval '1 day')) AND cpat.use_yn = 'Y' -- 구분코드 케이스문 넣기 !!
AND cpat.aply_tgt_no IN
#{item.goodsNo}
AND cpat.aply_tgt_ccd = '10'
AND ccb.cpn_kind_cd = #{cpnKindCd} AND ccb.degr_ccd = #{degrCcd} AND cci.mbr_no = #{mbrNo} AND cci.cpn_use_dt ISNULL
